mapper.insert:
INSERT INTO schedule (
                      title
                      , content
                      , color
                      , repeat
                      , start_t
                      , end_t)
VALUES ( #{title}
       , #{content}
       , #{color}
       , #{repeat}
       , #{startT}
       , #{endT})

INSERT INTO file (
                  post_id
                  , filename
                  , file_extension
                  , file_url)
VALUES ( #{postId}
       , #{filename}
       , #{fileExtension}
       , #{fileUrl})

mapper.update:
UPDATE schedule
SET title = #{title}
  , content = #{content}
  , color = #{color}
  , repeat = #{repeat}
  , start_t = #{startT}
  , end_t = #{endT}
WHERE id = #{id}

UPDATE file
SET filename = #{filename}
  , file_extension = #{fileExtension}
  , file_url = #{fileUrl}
WHERE post_id = #{postId}
